class Foo {
    enum MyEnum { A, B };

    void doSomething(MyEnum e) {
        switch(e) {
            case A -> System.out.println("a");
            case B -> System.out.println("b");
            default -> System.out.println("unnecessary default");
        };
    }
}

        ```

